import axios from 'axios';
import { useEffect, useReducer, useRef, useState } from 'react';

type Slide = {
  slide_id: string;
  slide_img: string;
  slide_main: Boolean;
};

type SlideState = {
  done: Boolean;
  value: Array<Slide>;
};

const slideReducer = function (state: SlideState, payload: Slide): SlideState {
  if (state.value.length < 5) {
    state.value.push(payload);
  } else {
    return { done: true, value: new Array(...state.value) };
  }

  return state;
};

const SlideShow = () => {
  const [clickedItem, setClickedItem] = useState<Slide | null>(null);

  var itemIndex = useRef(0);
  var loopStarted = useRef(false);
  var selectorClicked = useRef(false);

  const [state, dispatch] = useReducer(slideReducer, { done: false, value: [] });

  const selectorLoop = function (i: number, alt: boolean) {
    const progressEl = document.getElementById('slideshow-progress')!;

    if (!selectorClicked.current) {
      if (alt) {
        progressEl.style.scale = '';

        if (state.value.length >= itemIndex.current + 1) {
          itemIndex.current += 1;
        } else {
          itemIndex.current = 0;
        }

        console.log(itemIndex.current);

        setClickedItem(state.value.at(itemIndex.current)!);
      } else {
        progressEl.style.scale = '0 1';
      }

      setTimeout(() => {
        selectorLoop(i++, !alt);
      }, 3000 * i);
    } else {
      selectorClicked.current = false;
      setTimeout(() => {
        selectorLoop(i++, !alt);
      }, 6000 * i);
    }
  };

  useEffect(() => {
    if (state.value.length === 0) {
      axios
        .get<Slide[]>('/getslides')
        .then((res) => {
          res.data.forEach((v) => {
            dispatch(v);

            if (clickedItem === null) {
              setClickedItem(v);
            }
          });
        })
        .catch((err) => console.log(err));
    }

    if (!loopStarted.current) {
      selectorLoop(1, true);
      loopStarted.current = true;
    }
  }, []);

  const SlideSelector = (params: { id: string; img: string; main: Boolean }) => {
    return (
      <button
        className={`h-2 w-2 rounded-full transition-all duration-500 ${clickedItem?.slide_id === params.id || (params.main && clickedItem == null) ? 'ml-1 mr-1 scale-125 bg-slate-500' : 'ml-0.5 mr-0.5 bg-slate-300'}`}
        onClick={(e) => {
          setClickedItem({ slide_id: params.id, slide_img: params.img, slide_main: params.main });
          itemIndex.current = state.value.findIndex((el) => el.slide_id === clickedItem?.slide_id);
          console.log(itemIndex.current);

          selectorClicked.current = true;
        }}
      ></button>
    );
  };

  return (
    <div className="fit h-4/5 max-h-[900px] w-3/4 max-w-[1600px] rounded-md ">
      <img src={`/images/slides/${clickedItem?.slide_img}`} alt="SlideImage" className="h-[95%] w-full" />
      <div className="mt-1 flex h-1 w-full justify-center">
        <div id="slideshow-progress" className={`h-full w-52 rounded-md bg-slate-300 transition-all duration-[3s]`}></div>
      </div>
      <div className="flex h-3 w-full justify-center">
        <div className="mt-2 flex h-full w-28 items-center justify-center rounded-md">
          {state.value.map((val) => (
            <SlideSelector key={val.slide_id} img={val.slide_img} id={val.slide_id} main={val.slide_main} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default SlideShow;
