import { ReactElement, useState } from 'react';

type SelectorParams = {
  clicked: String;
  id: String;
};

const SlideShow = () => {
  const [clickedItem, setClickedItem] = useState('');

  return (
    <div className="fit h-4/5 max-h-[900px] w-3/4 max-w-[1600px] rounded-md ">
      <img src="/images/slide-key.png" alt="SlideImage" className="h-[95%] w-full" />
      <div className="flex h-3 w-full justify-center">
        <div className="mt-2 flex h-full w-28 items-center justify-center rounded-md">
          <SlideSelector clicked={clickedItem} id={'item-1'} />
          <SlideSelector clicked={clickedItem} id={'item-2'} />
          <SlideSelector clicked={clickedItem} id={'item-3'} />
          <SlideSelector clicked={clickedItem} id={'item-4'} />
          <SlideSelector clicked={clickedItem} id={'item-5'} />
        </div>
      </div>
    </div>
  );
};

const SlideSelector = (params: SelectorParams) => {
  return (
    <button
      className="ml-0.5 mr-0.5 h-2 w-2 rounded-full bg-slate-300"
      onClick={(e) => {
        e.currentTarget.id;
      }}
    ></button>
  );
};

export default SlideShow;
