import axios from 'axios';
import { useEffect, useReducer, useState } from 'react';

type Slide = {
  slide_id: string;
  slide_img: string;
  slide_main: Boolean;
};

type SlideState = {
  status: Boolean;
  value: Array<Slide>;
};

const SlideShow = () => {
  const [clickedItem, setClickedItem] = useState<String | null>(null);

  var slideReducer = function (state: SlideState, payload: Slide): SlideState {
    if (state.value.length < 5) {
      state.value.push(payload);
    }

    return state;
  };

  const [state, dispatch] = useReducer(slideReducer, { status: false, value: [] });

  useEffect(() => {
    if (state.length === 0) {
      console.log(state);
      axios
        .get<Slide[]>('/getslides')
        .then((res) => {
          res.data.forEach((v) => {
            dispatch(v);
          });
        })
        .catch((err) => console.log(err));
    }
  }, [state]);

  const SlideSelector = (params: { id: String; main: Boolean }) => {
    return (
      <button
        className={`h-2 w-2 rounded-full transition-all duration-500 ${clickedItem === params.id || (params.main && clickedItem == null) ? 'ml-1 mr-1 scale-125 bg-slate-500' : 'ml-0.5 mr-0.5 bg-slate-300'}`}
        onClick={(e) => {
          setClickedItem(params.id);
        }}
      ></button>
    );
  };

  console.log(state);

  return (
    <div className="fit h-4/5 max-h-[900px] w-3/4 max-w-[1600px] rounded-md ">
      <img src="/images/slides/slide-keyboard-1.png" alt="SlideImage" className="h-[95%] w-full" />
      <div className="flex h-3 w-full justify-center">
        <div className="mt-2 flex h-full w-28 items-center justify-center rounded-md">
          {state.map((val) => (
            <SlideSelector key={val.slide_id} id={val.slide_id} main={val.slide_main} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default SlideShow;
